document.addEventListener("DOMContentLoaded",function(){if("IntersectionObserver"in window){const e=document.querySelectorAll("img[data-src]"),n=new IntersectionObserver((e,n)=>{e.forEach(e=>{if(e.isIntersecting){const t=e.target;t.src=t.dataset.src,t.removeAttribute("data-src"),t.classList.add("lazy-loaded"),n.unobserve(t)}})},{rootMargin:"50px 0px",threshold:.01});e.forEach(e=>n.observe(e))}else{document.querySelectorAll("img[data-src]").forEach(e=>{e.src=e.dataset.src,e.removeAttribute("data-src")})}});const style=document.createElement("style");style.textContent="\n    img[data-src] {\n        opacity: 0;\n        transition: opacity 0.3s ease-in-out;\n    }\n    \n    img.lazy-loaded {\n        opacity: 1;\n    }\n    \n    /* Placeholder background while loading */\n    img[data-src] {\n        background: #f0f0f0;\n        background-image: linear-gradient(90deg, #f0f0f0 0px, #e0e0e0 40px, #f0f0f0 80px);\n        background-size: 200px 100%;\n        animation: shimmer 1.5s infinite;\n    }\n    \n    @keyframes shimmer {\n        0% { background-position: -200px 0; }\n        100% { background-position: 200px 0; }\n    }\n",document.head.appendChild(style);